import { Space, Table, Tag,Button } from 'antd';
import { useState } from 'react';
import { ArticlesApi } from '../request/api';

export default function ListTable() {
    const [rows,setRows] = useState([
        {
          key: '1',
          name: 'John Brown',
          date: '2022-10-31 15:07:25',
        },
      ])
    useEffect(()=>{
        ArticlesApi().then(res=>{
            console.log(res)
            setRows(res.data.arr)
        })
    },[])
    const columns = [].map(item=>{
        console.log(item)
        return {
            dataIndex: 'name',
            key: 'name',
            width:'60%',
            render: (text) => 
            <>
              <Link className='table_title' to='/'>{text.title}</Link>
              <p style={{color:"#999"}}>
                  {text}
              </p>
            </>
            
          }
    })
