import {/*  Avatar, */ Button, List, Skeleton, Pagination } from 'antd';
import moment from 'moment';
import React, { useEffect, useState } from 'react';
import { ArticlesApi } from '../request/api';


export default function ListList() {


  const [list, setList] = useState([]);
  const [total,setTotal] = useState(0)

  useEffect(() => {
    ArticlesApi().then(res => {
      console.log(res)
      if (res.errCode === 0) {
        let { arr, total, num, count } = res.data
        setTotal(total)
        setList(arr)
      }
    })

  }, [])






  return (
    <div className='list_table' style={{
      padding: '20px'
    }}>
      <List
        className="demo-loadmore-list"
        itemLayout="horizontal"
        dataSource={list}
        renderItem={(item) => {
          console.log(item)
          return (
            <List.Item
              actions={[<Button type='primary' onClick={(res) => { console.log(res) }}>编辑</Button>, <Button type='primary' danger>删除</Button>]}
            >
              <Skeleton
                title={false}
                loading={item.loading}
                active>
                <List.Item.Meta
                  title={<a href="#">{item.title}</a>}
                  description={item.subTitle}
                />
                <div>{
                  moment(item.data).format('YYYY-MM-DD hh:mm:ss')
                }</div>
              </Skeleton>
            </List.Item>
          )
        }
        }
      />
      <Pagination defaultCurrent={1} total={total} />
    </div>
  )
}
