import React, { useEffect } from 'react'
import { Link } from 'react-router-dom';
import './less/list_table.less'
import { Space, Table, /* Tag,  */Button } from 'antd';
import { useState } from 'react';
import { ArticlesApi } from '../request/api';
import MixTitle from '../components/MixTitle';
import moment from 'moment';

// const data = [{
//     key: '1',
//     name: 'John Brown',
//     date: '2022-10-31 15:07:25',
// }]

export default function ListTable() {
    const [rows, setRows] = useState([])
    
    // 封装请求
    const getArticleList = ()=>{
        ArticlesApi().then(res => {
            console.log(res.data.arr)
            // 深拷贝
            let newarr = JSON.parse(JSON.stringify(res.data.arr))
            let viewarticles = []
            newarr.map(item => {

                // item.data = moment(item.data).format('YYYY-MM-DD hh:mm:ss')
                // item.mixTitle = `<div>
                //     <Link className='table_title' to='/'>${item.title }</Link>
                //     <p style={{ color: "#999" }}>
                //     ${item.subTitle }
                //     </p>
                // </div>`

                // 组件化开发
                let obj = {
                    key: item.id,
                    date: moment(item.data).format('YYYY-MM-DD hh:mm:ss'),
                    mixTitle: <MixTitle id={item.id} title={item.title} subTitle={item.subTitle}></MixTitle>
                }
                viewarticles.push(obj)
            })
            console.log(viewarticles)
            setRows(viewarticles)

        })
    }
    
    useEffect(() => {
        getArticleList()
    }, [])

    const columns = [
        {
            dataIndex: 'mixTitle',
            key: 'mixTitle',
            width: '60%',
            render: (text) =>
                text
            // <>
            //     <Link className='table_title' to='/'>{ }</Link>
            //     <p style={{ color: "#999" }}>
            //         {text}
            //     </p>
            // </>
            ,
        },
        {
            dataIndex: 'date',
            key: 'date',
        },
        {
            key: 'action',
            render: (text) => {
                console.log(text)
                return (
                    <Space size="middle">
                        <Button type='primary' onClick={()=>console.log(text.key)}>编辑</Button>
                        <Button type='primary' onClick={()=>console.log(text.key)}>删除</Button>
                    </Space>
                )
            },
        },
    ];

    return (
        <div className='list_table'>
            <Table showHeader={false} columns={columns} dataSource={rows} />
        </div>
    )
}
