import React,{useEffect} from 'react'
import { Link } from 'react-router-dom';
import'./less/list_table.less'
import { Space, Table, Tag,Button } from 'antd';
import { useState } from 'react';
import { ArticlesApi } from '../request/api';

export default function ListTable() {
    const [rows,setRows] = useState()
    useEffect(()=>{
        ArticlesApi().then(res=>{
            console.log(res)
            setRows(res.data.arr)
        })
    },[])
    
    const columns = [
        {
          dataIndex: 'name',
          key: 'id',
          width:'60%',
          render: (text) => 
          <>
            <Link className='table_title' to='/'>{}</Link>
            <p style={{color:"#999"}}>
                {text}
            </p>
          </>
          ,
        },
        {
          dataIndex: 'date',
          key: 'date',
        },
        {
          key: 'action',
          render: (_, record) => (
            <Space size="middle">
                <Button type='primary'>编辑</Button>
                <Button type='primary' danger={true}>删除</Button>
            </Space>
          ),
        },
      ];
      
  return (
    <div className='list_table'>
        <Table showHeader={false} columns={columns} dataSource={rows} />
    </div>
  )
}
