import React,{useEffect, useState} from 'react'
import logoImg from '../asserts/logo.png'
import defaultAvatar from '../asserts/defaultAvatar.jpg'
import { CaretDownOutlined, /* SmileOutlined */ } from '@ant-design/icons';
import { Dropdown, Menu, Space } from 'antd';
import { useNavigate } from 'react-router-dom';


export default function Header() {
    const [avatar,setAvatar] = useState(defaultAvatar)
    const [username,setUsername]= useState('游客')
    const navigate = useNavigate()
    
    useEffect(()=>{
        let username1 = localStorage.getItem('username')
        let avatar1 = localStorage.getItem('avatar')
        if(username1){
            setUsername(username1)
        }
        if(avatar1){
            console.log(avatar1)
            setAvatar('http://47.93.114.103:6688/'+avatar1)
        }
    },[])
    
    // 退出登录
    const logout = ()=>{
        navigate('/login')
    }

    const menu = (
        <Menu
            items={[
                {
                    key: '1',
                    // onClick:,
                    label: (
                        // <a target="_blank" rel="noopener noreferrer" href="/edit">
                        //     修改资料
                        // </a>
                        修改资料
                    ),
                }, 
                {
                    type: 'divider',
                },
                {
                    key: '2',
                    onClick:logout,
                    label: (
                        // <a target="_blank" rel="noopener noreferrer" href="/login">
                        //     退出登录
                        // </a>
                        退出登录
                    ),
                    //   icon: <SmileOutlined />,
                    //   disabled: true,
                },

            ]}
        />
    );
    return (
        <header>
            <img src={logoImg} alt="" className='logo' />
            <div className="right">
                <Dropdown overlay={menu}>
                    <a className='ant-dropdown-link' onClick={(e) => e.preventDefault()}>
                        <Space>
                            <img src={avatar} 
                            className='avatar' alt="" />
                            <span>{username}</span>
                            <CaretDownOutlined />
                            {/* <DownOutlined /> */}
                        </Space>
                    </a>
                </Dropdown>
            </div>
        </header>
    )
}
