import React, { useEffect } from 'react'
import { Link } from 'react-router-dom';
import './less/list_table.less'
import { Space, Table, /* Tag,  */Button } from 'antd';
import { useState } from 'react';
import { ArticlesApi } from '../request/api';
// import m

// const data = [{
//     key: '1',
//     name: 'John Brown',
//     date: '2022-10-31 15:07:25',
// }]

export default function ListTable() {
    const [rows, setRows] = useState([
        {
            key: '1',
            name: 'John Brown',
            subName: '',
            date: '2022-10-31 15:07:25',
        }
    ])
    useEffect(() => {
        ArticlesApi().then(res => {
            console.log(res.data.arr)
            // 深拷贝
            let newarr = JSON.parse(JSON.stringify(res.data.arr))
            newarr.map(item => {
                item.key = item.id
                // item.data = moment(item.data).format('YYYY-MM-DD hh:mm:ss')
                item.mixTitle = `
                <>
                    <Link className='table_title' to='/'>${item.title }</Link>
                    <p style={{ color: "#999" }}>
                    ${item.subTitle }
                    </p>
                </>
                
                `
            })
            console.log(newarr)
            // setRows(res.data.arr)

        })
    }, [])

    const columns = [
        {
            dataIndex: 'mixTitle',
            key: 'mixTitle',
            width: '60%',
            render: (text) =>
                text    
            // <>
                //     <Link className='table_title' to='/'>{ }</Link>
                //     <p style={{ color: "#999" }}>
                //         {text}
                //     </p>
                // </>
            ,
        },
        {
            dataIndex: 'date',
            key: 'date',
        },
        {
            key: 'action',
            render: (_, record) => (
                <Space size="middle">
                    <Button type='primary'>编辑</Button>
                    <Button type='primary' danger={true}>删除</Button>
                </Space>
            ),
        },
    ];

    return (
        <div className='list_table'>
            <Table showHeader={false} columns={columns} dataSource={rows} />
        </div>
    )
}
