import React, { useEffect, useState } from 'react'
import './less/Means.less'

import { Button, Form, Input, message } from 'antd';

import { GetInfoApi } from '../request/api';

export default function Means() {
  // const [password,setPassWord] = useState('')

  useEffect(() => {
    GetInfoApi().then(res => {
      if(res.errCode===0){
        // setXxx是异步的
        // setU_Name(res.data.username)
        // // setPassWord(res.data.password)
        // setAvatar(res.data.avatar)
        // 存到sessionStorage
        sessionStorage.setItem('username',res.data.username)
        sessionStorage.setItem('avatar',res.data.avatar)
      }
    })
  }, [])

  const onFinish = (values)=>{
    console.log(values)
    // 对比sessionStorage
    // 如果表单的username有值，并且不等于初始化拿到的username
    if(values.username && values.username!=sessionStorage.getItem('username')&&values.password){
      message.success('准备修改中')
    }else{
      message.error('请检查输入')
    }
  }

  return (
    <div className="means">
      <Form
        name="basic"
        style={{
          width: '400px'
        }}
        initialValues={{
        }}
        onFinish={onFinish}
        autoComplete="off"
      >
        <Form.Item
          label="修改用户名: "
          name="username"
          rules={[
            {
              required: true,
              message: 'Please input your username!',
            },
          ]}
        >
          <Input />
        </Form.Item>

        <Form.Item
          label="修 改 密 码: "
          name="password"

          rules={[
            {
              required: true,
              message: 'Please input your password!',
            },
          ]}
        >
          <Input.Password />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" style={{
            float: 'right'
          }}>
            修改
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
}
